          resolve\(JSON.parse\(body\)\);
        } catch {
          resolve\(body\);
        }
      }\);
    }\);

    req.on\(''error'', \(e\) => {
      console.error\(''Request error:'', e.message\);
      reject\(e\);
    }\);

    if \(data\) req.write\(JSON.stringify\(data\)\);
    req.end\(\);
  }\);
